import React, { Component } from 'react';
import axios from 'axios';

export default class EditFood extends Component {
  constructor(props) {
    super(props);

    this.onChangeFoodname = this.onChangeFoodname.bind(this);
    this.onChangePrice = this.onChangePrice.bind(this);
    this.onChangeSalePrice = this.onChangeSalePrice.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      Foodname: '',
      Price: '',
      SalePrice: 0,
    }
  }

  componentDidMount() {
    axios.get('http://localhost:5000/foods/'+this.props.match.params.id)
      .then(response => {
        this.setState({
          Foodname: response.data.Foodname,
          Price: response.data.Price,
          SalePrice: response.data.SalePrice     //LOOK AT edit-exercice this is where they connected two databases
        })   
      })
      .catch(function (error) {
        console.log(error);
      })
  }

  onChangeFoodname(e) {
    this.setState({
      Foodname: e.target.value
    })
  }

  onChangePrice(e) {
    this.setState({
      Price: e.target.value
    })
  }

  onChangeSalePrice(e) {
    this.setState({
      SalePrice: e.target.value
    })
  }



  onSubmit(e) {
    e.preventDefault();

    const Food = {
      Foodname: this.state.Foodname,
      Price: this.state.Price,
      SalePrice: this.state.SalePrice,
    }

    console.log(Food);

    axios.post('http://localhost:5000/foods/update/' + this.props.match.params.id, Food)
      .then(res => console.log(res.data));

    window.location = '/';
  }

  render() {
    return (
    <div>
      <h3>Edit Food Log</h3>
      <form onSubmit={this.onSubmit}>

        <div className="form-group"> 
          <label>Foodname: </label>
          <input  type="text"
              required
              className="form-control"
              value={this.state.Foodname}
              onChange={this.onChangeFoodname}
              />
        </div>

        <div className="form-group"> 
          <label>Price: </label>
          <input  type="text"
              required
              className="form-control"
              value={this.state.Price}
              onChange={this.onChangePrice}
              />
        </div>

        <div className="form-group">
          <label>SalePrice (in minutes): </label>
          <input 
              type="text" 
              className="form-control"
              value={this.state.SalePrice}
              onChange={this.onChangeSalePrice}
              />
        </div>
        <div className="form-group">
          <input type="submit" value="Edit Food Log" className="btn btn-primary" />
        </div>
      </form>
    </div>
    )
  }
}
